// OpenAI API 服务
const API_KEY = 'sk-d606624ebfc94b488646a45852226453'

// 根据环境选择 API URL
// 开发环境使用代理，生产环境需要配置后端代理
const isDevelopment = process.env.NODE_ENV === 'development'
const API_URL = isDevelopment 
  ? '/api/openai/v1/chat/completions'  // 开发环境使用代理
  : 'https://api.openai.com/v1/chat/completions'  // 生产环境需要后端代理

/**
 * 调用 OpenAI API 进行对话
 * @param {string} message - 用户消息
 * @param {Array} conversationHistory - 对话历史记录
 * @returns {Promise<string>} AI 回复
 */
export async function chatWithAI(message, conversationHistory = []) {
  try {
    // 构建消息历史，包含系统提示和对话历史
    const messages = [
      {
        role: 'system',
        content: '你是一个专业的美食助手，擅长推荐餐厅、菜品，回答美食相关问题。你的回答要友好、专业，并且要像真人一样自然交流，不要像机器人。你可以根据用户的喜好和需求，给出个性化的建议。'
      },
      ...conversationHistory,
      {
        role: 'user',
        content: message
      }
    ]

    const response = await fetch(API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${API_KEY}`
      },
      body: JSON.stringify({
        model: 'gpt-3.5-turbo',
        messages: messages,
        temperature: 0.7,
        max_tokens: 500
      })
    })

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}))
      const errorMessage = errorData.error?.message || `API 错误: ${response.status} ${response.statusText}`
      console.error('API 响应错误:', errorData)
      throw new Error(errorMessage)
    }

    const data = await response.json()
    return data.choices[0].message.content.trim()
  } catch (error) {
    console.error('OpenAI API 错误:', error)
    // 提供更详细的错误信息
    if (error.message.includes('Load failed') || error.message.includes('Failed to fetch')) {
      throw new Error('网络连接失败，可能是 CORS 问题。请确保开发服务器已重启，或检查网络连接。')
    }
    throw error
  }
}
